import axios from "axios";
import { useContext, useEffect, useState } from "react";
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
import { Context } from "../../context/Context";
import "./singlePost.css";

export default function SinglePost() {
  const location = useLocation();
  const path = location.pathname.split("/")[2];
  const [post, setPost] = useState({})
  const PF = "localhost:4000/images/";
  const {user} = useContext(Context);
  const [title, setTitle] = useState("");
  const [desc, setDesc] = useState("");
  const [updateMode, setUpdateMode] = useState(false);



  useEffect(()=>{
    const getPost = async ()=>{
      const res = await axios.get("/posts/" + path); //Getting a post
      setPost(res.data);
      setTitle(res.data.title);
      setDesc(res.data.desc);
    };
    getPost();
  },[path])

  const handleDelete = async () =>{
    try {
      console.log(path);
      await axios.delete("/posts/" + path, {data:{username: user.username}});
      window.location.replace("/")
    } catch (error) {
      console.log(error);
    }

  }

  return (
    <div className="singlePost">
      <div className="singlePostWrapper">
        {post.photo && (
          <img
          className="singlePostImg"
          src={PF + post.photo}
          alt="single Post Image"
        />
        ) }{
          updateMode ? (<input type="text" value={title} className="singlePostTitleInput" autoFocus 
          onChange={(e)=> setTitle(e.target.value)}
           />) :(

          
        <h1 className="singlePostTitle">
          {title}
          {post.username === user?.username &&
            <div className="singlePostEdit">
            <i className="singlePostIcon fa-solid fa-pen-to-square" onClick={()=>setUpdateMode(true)} ></i>
            <i className="singlePostIcon fa-solid fa-trash" onClick={handleDelete}></i>
          </div>
          }
        </h1>
          )}
        <div className="singlePostInfo">
          <span className="singlePostAuthor">
            Author:
            <Link to={`/?user=${post.username}`} className="link"> <b>{post.username}</b></Link>
            
          </span>
          <span className="singlePostDate">{ new Date(post.createdAt).toDateString()}</span>
        </div>
        {updateMode ? (
          <textarea className="singlePostDesInput" value={desc} onChange={(e)=> setDesc(e.target.value)} />
        ) : (
          <p className="singlePostDes">{post.desc}</p>
        )}
        
      </div>
    </div>
  );
}
