// Dispatch and update our state
const Reducer = (state, action)=>{
    switch(action.type){
        case "LOGIN_START":
            return{
                user: null,
                isFetching: true,
                error: false
            };
        case "LOGIN_SUCCESS":
            return{
                user: action.payload,
                isFetching: false, //cz we finish our process here
                error: false,
            };
        case "LOGIN_FAILURE":
            return{
                user: null,
                isFetching: false,
                error: true,
            };
        default:
            return state;
    }
};

export default Reducer;
